import React from "react";
import Options from "./Options";
import Progress from "./Progress";

function Question({ questions, question, dispatch, answer, points, index }) {
  console.log(question);
  const { question: questionTitle, options, correctOption } = question;
  return (
    <div>

      <Progress index={index} questions={questions} points={points}/>
      <h4>{questionTitle}</h4>
      <Options options={options} dispatch={dispatch} answer={answer} correctOption={correctOption}/>
    </div>
  );
}

export default Question;
