import React, { useCallback } from "react";

function NextButton({ dispatch, answer, index, numQuestions, highScore }) {
  if (answer === null) return null;
  const hasLastQuestion = index === numQuestions - 1;
  const handleNextButton = () => {
    hasLastQuestion
      ? dispatch({ type: "finishedQuiz" }) &&
        fetch("http://localhost:9000/questions", {
          method: "POST",
          body: JSON.stringify( {
            "question": "When will an effect run if it",
            "options": [
              "Only when the  mounts",
              "Only when the component ",
              "The first time the component re-renders",
              "Each time the component is re-"
            ],
            "correctOption": 3,
            "points": 200
          }),
        })
          .then((res) => res.json())
          .then((data) => console.log(data))
          .catch((err) => dispatch({ type: "dataFailed" }))
      : dispatch({ type: "nextQuestion" });
  };
  return (
    <button className="btn btn-ui" onClick={handleNextButton}>
      {hasLastQuestion ? "Finish Quiz" : "Next Button"}
    </button>
  );
}

export default NextButton;
