import { createContext, useEffect, useReducer } from "react";
import { reducer } from "./reducer";
// import { initialState } from "./initial";

const initialState = {
    questions: [],
  
    // 'loading, 'error', 'ready', 'active', 'finished'
    status: "loading",
    index: 0,
    answer: null,
    points: 0,
    highScore: 0,
    secondsRemaining: 0,
    filterQuestions: [],
    questionsLevel: questionsLevelObj.all,
  };

const QuizContext = createContext()

export const QuizProvider = ({children}) => {
    const [ { questions, status, index, answer, points, highScore, secondsRemaining }, dispatch] = useReducer(reducer, initialState)

    const numQuestions = questions.length;
    const maxPossiblePoints = questions.reduce(
      (prev, cur) => prev + cur.points,
      0
    );
  
    useEffect(function () {
      fetch("http://localhost:9000/questions")
        .then((res) => res.json())
        .then((data) => dispatch({ type: "dataReceived", payload: data }))
        .catch((err) => dispatch({ type: "dataFailed" }));
    }, []);

    return <QuizProvider.Provider value={{questions, status, index, answer, points, highScore, secondsRemaining, numQuestions, maxPossiblePoints, dispatch}}>{children}</QuizProvider.Provider>
}

export default QuizContext
