import React from 'react'

function Question({ question }) {
  console.log(question)
  const { question: questionTitle, options, correctOption, points } = question
  return (
    <div>
      <h4>{questionTitle}</h4>
      <div className='options'>      {options.map((option) => <button className='btn btn-option'>{option}</button>)}
</div>
    </div>
  )
}

export default Question
